void update_time_dial_blackboard(int second, int minute, int hour, bool mode, bool am, int day, int month, int year, int weekday)
{
#ifdef ENABLE_FACE_DIAL_BLACKBOARD
    if (!face_dial_blackboard)
    {
        return;
    }
	lv_img_set_src(face_dial_blackboard_12_90735, face_dial_blackboard_dial_img_12_90735_group[((month - 1) / 1) % 12]);
	lv_img_set_src(face_dial_blackboard_14_96385, face_dial_blackboard_dial_img_14_96385_group[((weekday + 6) / 1) % 7]);
	lv_img_set_src(face_dial_blackboard_20_89223, face_dial_blackboard_dial_img_20_89223_group[(day / 1) % 10]);
	lv_img_set_src(face_dial_blackboard_21_89223, face_dial_blackboard_dial_img_20_89223_group[(day / 10) % 10]);
	lv_img_set_src(face_dial_blackboard_34_103067, face_dial_blackboard_dial_img_34_103067_group[(hour / 1) % 10]);
	lv_img_set_src(face_dial_blackboard_35_103067, face_dial_blackboard_dial_img_34_103067_group[(hour / 10) % 10]);
	lv_img_set_src(face_dial_blackboard_36_123509, face_dial_blackboard_dial_img_36_123509_group[(minute / 1) % 10]);
	lv_img_set_src(face_dial_blackboard_37_113241, face_dial_blackboard_dial_img_37_113241_group[(minute / 10) % 10]);

#endif
}

void update_weather_dial_blackboard(int temp, int icon)
{
#ifdef ENABLE_FACE_DIAL_BLACKBOARD
    if (!face_dial_blackboard)
    {
        return;
    }
	lv_img_set_src(face_dial_blackboard_2_88113, face_dial_blackboard_dial_img_weather[icon % 8]);

#endif
}

void update_status_dial_blackboard(int battery, bool connection){
#ifdef ENABLE_FACE_DIAL_BLACKBOARD
    if (!face_dial_blackboard)
    {
        return;
    }
	lv_img_set_src(face_dial_blackboard_15_924, face_dial_blackboard_dial_img_15_924_group[(battery / (100 / 11)) % 11]);
	lv_img_set_src(face_dial_blackboard_16_88469, face_dial_blackboard_dial_img_16_88469_group[(battery / 10) % 10]);
	lv_img_set_src(face_dial_blackboard_17_88469, face_dial_blackboard_dial_img_16_88469_group[(battery / 100) % 10]);
	if (battery < 100)
	{
		lv_obj_add_flag(face_dial_blackboard_17_88469, LV_OBJ_FLAG_HIDDEN);
	} else {
		lv_obj_clear_flag(face_dial_blackboard_17_88469, LV_OBJ_FLAG_HIDDEN);
	}
	lv_img_set_src(face_dial_blackboard_18_88469, face_dial_blackboard_dial_img_16_88469_group[(battery / 1000) % 10]);

#endif
}

