void update_all_dial_blackboard(int second, int minute, int hour, bool mode, bool am, int day, int month, int year, int weekday, 
    int temp, int icon, int battery, bool connection, int steps, int distance, int kcal, int bpm, int oxygen)
{
#ifdef ENABLE_FACE_DIAL_BLACKBOARD
    update_time_dial_blackboard(second, minute, hour, mode, am, day, month, year, weekday);
    update_weather_dial_blackboard(temp, icon);
    update_status_dial_blackboard(battery, connection);
    update_activity_dial_blackboard(steps, distance, kcal);
    update_health_dial_blackboard(bpm, oxygen);
#endif
}

void update_check_dial_blackboard(lv_obj_t *root, int second, int minute, int hour, bool mode, bool am, int day, int month, int year, int weekday, 
    int temp, int icon, int battery, bool connection, int steps, int distance, int kcal, int bpm, int oxygen)
{
#ifdef ENABLE_FACE_DIAL_BLACKBOARD
    if (root != face_dial_blackboard)
    {
        return;
    }
    update_time_dial_blackboard(second, minute, hour, mode, am, day, month, year, weekday);
    update_weather_dial_blackboard(temp, icon);
    update_status_dial_blackboard(battery, connection);
    update_activity_dial_blackboard(steps, distance, kcal);
    update_health_dial_blackboard(bpm, oxygen);
#endif
}


